import React from 'react';
import { useParams, useNavigate } from 'react-router-dom';
import { articles } from '../data/articles';
import { ArrowLeft, Clock, User } from 'lucide-react';

const Article = () => {
  const { slug } = useParams();
  const navigate = useNavigate();
  const article = articles.find((a) => a.slug === slug);

  if (!article) return <p className="p-8">Το άρθρο δεν βρέθηκε.</p>;

  return (
    <div className="min-h-screen bg-gradient-to-br from-sky-50 to-sky-100 py-12 px-4">
      <article className="max-w-3xl mx-auto bg-white rounded-2xl shadow-lg p-8">
        <button onClick={() => navigate(-1)} className="flex items-center gap-2 text-sky-600 mb-6">
          <ArrowLeft className="w-4 h-4" /> Πίσω
        </button>

        <div className="flex items-center gap-4 text-sm text-sky-700 mb-4">
          <span className="flex items-center gap-1"><User className="w-4 h-4" /> {article.author}</span>
          <span className="flex items-center gap-1"><Clock className="w-4 h-4" /> {article.readTime} λεπτά</span>
        </div>

        <h1 className="text-3xl font-bold text-slate-800 mb-4">{article.title}</h1>
        <p className="text-slate-600 leading-relaxed">{article.excerpt}</p>
      </article>
    </div>
  );
};

export default Article;
