import React from 'react';
import { useParams, useNavigate } from 'react-router-dom';
import { Clock, User, ArrowLeft } from 'lucide-react';
import { articles } from '../data/articles';

const Article = () => {
  const { slug } = useParams();
  const navigate = useNavigate();
  const article = articles.find((a) => a.slug === slug);

  if (!article) return <p className="p-8">Το άρθρο δεν βρέθηκε.</p>;

  return (
    <div className="min-h-screen bg-gradient-to-br from-sky-50 to-sky-100 py-12 px-4">
      <article className="max-w-3xl mx-auto bg-white rounded-2xl shadow-lg overflow-hidden">
        {/* Hero placeholder */}
        <div className="h-64 bg-sky-200 flex items-center justify-center text-sky-600 font-semibold">
          Εικόνα Άρθρου
        </div>

        <div className="p-8 md:p-10">
          <button
            onClick={() => navigate(-1)}
            className="flex items-center gap-2 text-sky-600 hover:text-sky-700 mb-6 transition"
          >
            <ArrowLeft className="w-4 h-4" /> Πίσω
          </button>

          <div className="flex flex-wrap items-center gap-4 text-sm text-sky-700 mb-4">
            <span className="flex items-center gap-1"><User className="w-4 h-4" /> {article.author}</span>
            <span className="flex items-center gap-1"><Clock className="w-4 h-4" /> {article.readTime} λεπτά</span>
          </div>

          <h1 className="text-3xl md:text-4xl font-bold text-slate-800 mb-4">{article.title}</h1>
          <p className="text-slate-600 leading-relaxed">{article.excerpt}</p>

          <div className="mt-8 p-6 bg-sky-50 rounded-xl border border-sky-100 text-center">
            <p className="text-sky-800 font-medium mb-3">Έχετε ερωτήσεις;</p>
            <button
              onClick={() => navigate('/contact')}
              className="inline-flex items-center gap-2 bg-sky-600 text-white px-5 py-2.5 rounded-lg hover:bg-sky-700 transition"
            >
              Επικοινωνήστε μαζί μας
            </button>
          </div>
        </div>
      </article>
    </div>
  );
};

export default Article;
