import React from 'react';

const Button = ({
  children,
  variant = 'sea',      // βασικό θαλασσί
  size = 'md',
  disabled = false,
  onClick,
  type = 'button',
  className = ''
}) => {
  const baseStyles =
    'font-semibold rounded-lg transition-all duration-200 ' +
    'focus:outline-none focus:ring-2 focus:ring-offset-2 active:scale-95';

  // analogous palette: cyan → teal → green
  const variants = {
    sea:      'bg-cyan-600 text-white hover:bg-cyan-700 focus:ring-cyan-800',
    teal:     'bg-teal-600 text-white hover:bg-teal-700 focus:ring-teal-800',
    mint:     'bg-emerald-500 text-white hover:bg-emerald-600 focus:ring-emerald-700',
    lime:     'bg-lime-500 text-gray-900 hover:bg-lime-600 focus:ring-lime-700',
    // ήπια εναλλακτικά
    softSea:  'bg-cyan-100 text-cyan-700 hover:bg-cyan-200 focus:ring-cyan-300',
    softMint: 'bg-emerald-100 text-emerald-700 hover:bg-emerald-200 focus:ring-emerald-300'
  };

  const sizes = {
    sm: 'px-3 py-1.5 text-sm',
    md: 'px-5 py-2.5',
    lg: 'px-7 py-3 text-base'
  };

  const disabledStyles = disabled ? 'opacity-55 cursor-not-allowed' : '';

  return (
    <button
      type={type}
      onClick={onClick}
      disabled={disabled}
      className={`${baseStyles} ${variants[variant]} ${sizes[size]} ${disabledStyles} ${className}`}
    >
      {children}
    </button>
  );
};

export default Button;
