import React, { useEffect, useState } from 'react';
import {
  PawPrint,
  Mail,
  Phone,
  MapPin,
  FileText,
  Shield,
  HelpCircle,
  Users,
  BookOpen,
  UserCheck,
  Sun,
  Moon,
  Send
} from 'lucide-react';

const Footer = () => {
  const [theme, setTheme] = useState('system');
  const [email, setEmail] = useState('');
  const [ok, setOk] = useState(false);

  // Detect user/system preference
  useEffect(() => {
    const stored = localStorage.getItem('theme');
    const prefers = window.matchMedia('(prefers-color-scheme: dark)').matches
      ? 'dark'
      : 'light';
    const initial = stored || prefers;
    setTheme(initial);
    document.documentElement.classList.toggle('dark', initial === 'dark');
  }, []);

  const toggleTheme = () => {
    const next = theme === 'dark' ? 'light' : 'dark';
    setTheme(next);
    localStorage.setItem('theme', next);
    document.documentElement.classList.toggle('dark', next === 'dark');
  };

  const handleNewsletter = (e) => {
    e.preventDefault();
    if (!email) return;
    // HoneyPot: αν συμπληρωθεί το πεδίο "fax" → spam
    if (e.target.fax.value) return;
    // mock POST
    fetch('/api/newsletter', {
      method: 'POST',
      headers: { 'Content-Type': 'application/json' },
      body: JSON.stringify({ email })
    })
      .then(() => {
        setOk(true);
        setEmail('');
        setTimeout(() => setOk(false), 4000);
      })
      .catch(() => alert('Κάτι πήγε στραβά. Δοκίμασε ξανά!'));
  };

  return (
    <footer className="bg-gray-100 dark:bg-gray-900 text-gray-800 dark:text-gray-200 transition-colors">
      <div className="max-w-7xl mx-auto px-4 py-10 grid gap-8 md:grid-cols-2 lg:grid-cols-4">
        {/* 1) Brand + Newsletter */}
        <div className="flex flex-col">
          <div className="flex items-center space-x-2 mb-3">
            <PawPrint className="w-6 h-6 text-indigo-600" />
            <span className="text-xl font-bold">PetBooking</span>
          </div>
          <p className="text-sm mb-4">
            Φροντίδα για όλα τα κατοικίδιά σας με αγάπη και επαγγελματισμό.
          </p>

          <form onSubmit={handleNewsletter} className="mt-auto space-y-2">
            <label htmlFor="news-email" className="text-sm font-medium">
              Newsletter & συμβουλές για κατοικίδια
            </label>
            <div className="flex gap-2">
              <input
                id="news-email"
                type="email"
                value={email}
                onChange={(e) => setEmail(e.target.value)}
                placeholder="το email σου"
                className="flex-1 px-3 py-2 rounded-lg border border-gray-300 dark:border-gray-700 bg-white dark:bg-gray-800 focus:outline-none focus:ring-2 focus:ring-indigo-500"
                required
              />
              {/* HoneyPot */}
              <input name="fax" type="text" className="hidden" tabIndex={-1} autoComplete="off" />
              <button
                type="submit"
                aria-label="Εγγραφή"
                className="px-3 py-2 rounded-lg bg-indigo-600 hover:bg-indigo-700 text-white transition"
              >
                <Send className="w-5 h-5" />
              </button>
            </div>
            {ok && (
              <p className="text-xs text-green-600 dark:text-green-400">Ευχαριστούμε για την εγγραφή!</p>
            )}
          </form>
        </div>

        {/* 2) Υπηρεσίες */}
        <div>
          <h3 className="font-semibold mb-3">Υπηρεσίες</h3>
          <ul className="space-y-2 text-sm">
            <li><a href="/hosting" className="hover:text-indigo-600 dark:hover:text-indigo-400 transition">Φιλοξενία</a></li>
            <li><a href="/dog-walk" className="hover:text-indigo-600 dark:hover:text-indigo-400 transition">Σκυλοβόλτα</a></li>
            <li><a href="/cat-home" className="hover:text-indigo-600 dark:hover:text-indigo-400 transition">Cat’οίκον επίσκεψη</a></li>
            <li><a href="/grooming" className="hover:text-indigo-600 dark:hover:text-indigo-400 transition">Grooming κατ’ οίκον</a></li>
            <li><a href="/vet-visit" className="hover:text-indigo-600 dark:hover:text-indigo-400 transition">Επίσκεψη στον κτηνίατρο</a></li>
          </ul>
        </div>

        {/* 3) Χρήσιμα links */}
        <div>
          <h3 className="font-semibold mb-3">Χρήσιμα</h3>
          <ul className="space-y-2 text-sm">
            <li className="flex items-center gap-2"><BookOpen className="w-4 h-4" /><a href="/blog" className="hover:text-indigo-600 dark:hover:text-indigo-400 transition">Άρθρα</a></li>
            <li className="flex items-center gap-2"><Users className="w-4 h-4" /><a href="/sitters" className="hover:text-indigo-600 dark:hover:text-indigo-400 transition">Εγγεγραμμένοι Sitters</a></li>
            <li className="flex items-center gap-2"><FileText className="w-4 h-4" /><a href="/terms" className="hover:text-indigo-600 dark:hover:text-indigo-400 transition">Όροι χρήσης</a></li>
            <li className="flex items-center gap-2"><Shield className="w-4 h-4" /><a href="/refunds" className="hover:text-indigo-600 dark:hover:text-indigo-400 transition">Πολιτική επιστροφών</a></li>
            <li className="flex items-center gap-2"><UserCheck className="w-4 h-4" /><a href="/owner-requirements" className="hover:text-indigo-600 dark:hover:text-indigo-400 transition">Προϋποθέσεις Ιδιοκτήτη</a></li>
            <li className="flex items-center gap-2"><UserCheck className="w-4 h-4" /><a href="/sitter-requirements" className="hover:text-indigo-600 dark:hover:text-indigo-400 transition">Προϋποθέσεις Sitter</a></li>
            <li className="flex items-center gap-2"><HelpCircle className="w-4 h-4" /><a href="/faq" className="hover:text-indigo-600 dark:hover:text-indigo-400 transition">FAQ</a></li>
          </ul>
        </div>

        {/* 4) Επικοινωνία + χάρτης + theme-toggle */}
        <div>
          <h3 className="font-semibold mb-3">Επικοινωνία</h3>
          <ul className="space-y-2 text-sm">
            <li className="flex items-center gap-2"><Phone className="w-4 h-4" /><a href="tel:+302101234567" className="hover:text-indigo-600 dark:hover:text-indigo-400 transition">(+30) 210 123 4567</a></li>
            <li className="flex items-center gap-2"><Mail className="w-4 h-4" /><a href="mailto:info@myanimal.gr" className="hover:text-indigo-600 dark:hover:text-indigo-400 transition">info@myanimal.gr</a></li>
            <li className="flex items-start gap-2"><MapPin className="w-4 h-4 mt-1" /><span>Αθήνα, Ελλάδα</span></li>
          </ul>

          {/* Mini-map */}
          <div className="mt-4 rounded-lg overflow-hidden ring-1 ring-gray-300 dark:ring-gray-700">
            <iframe
              title="Χάρτης"
              src="https://www.openstreetmap.org/export/embed.html?bbox=23.7165,37.9753,23.7405,37.9873&amp;layer=mapnik"
              className="w-full h-32"
            />
          </div>

          {/* Dark / light toggle */}
          <div className="mt-4 flex items-center justify-between text-sm">
            <span>Θέμα</span>
            <button
              onClick={toggleTheme}
              aria-label="Toggle theme"
              className="p-2 rounded-full hover:bg-gray-200 dark:hover:bg-gray-700 transition"
            >
              {theme === 'dark' ? <Sun className="w-5 h-5" /> : <Moon className="w-5 h-5" />}
            </button>
          </div>
        </div>
      </div>

      {/* Bottom bar */}
      <div className="border-t border-gray-300 dark:border-gray-800 mt-8 pt-4 text-center text-xs text-gray-500 dark:text-gray-400">
        <p>&copy; {new Date().getFullYear()} PetBooking – MyAnimal.gr. All rights reserved.</p>
      </div>
    </footer>
  );
};

export default Footer;
