import React from 'react';
import { Routes, Route } from 'react-router-dom';
import { AuthProvider } from './context/AuthContext';

/* ------- σελίδες ------- */
import Home from './pages/Home';
import Profile from './pages/Profile';
import MyBookings from './pages/MyBookings';
import NotFound from './pages/NotFound';
import Articles from './pages/Articles';
import Sitters from './pages/Sitters';
import Booking from './pages/Booking';
import Contact from './pages/Contact';
import Areas from './pages/Areas';
import AreaSitters from './pages/AreaSitters';

/* ------- components ------- */
import Login from './components/auth/Login.jsx';
import Register from './components/auth/Register.jsx';
import ForgotPassword from './components/auth/ForgotPassword.jsx';
import Header from './components/common/Header';
import Footer from './components/common/Footer';

function App() {
  return (
    <AuthProvider>
    <div className="fixed inset-0 z-0 pointer-events-none overflow-hidden">
      {/* 1️⃣ Κόκκινα – καρδιές & κολάρα */}
      <span className="shape top-[10%] left-[8%] scale-125 drop-shadow-lg" style={{ animationDelay: '0s', rotate: '-12deg' }}>
    <svg width="40" height="40" viewBox="0 0 64 64">
      <defs>
        <linearGradient id="g1" x1="0%" y1="0%" x2="100%" y2="100%">
          <stop offset="0%" stopColor="#f87171"/>
          <stop offset="100%" stopColor="#ef4444"/>
        </linearGradient>
      </defs>
      <path fill="url(#g1)" d="M32 58l-4-4C15 40 4 30 4 18 4 9 11 2 20 2c5 0 9 2 12 6 3-4 7-6 12-6 9 0 16 7 16 16 0 12-11 22-24 38z"/>
    </svg>
  </span>
      <span className="shape top-[75%] right-[10%] scale-110 drop-shadow-lg" style={{ animationDelay: '4s', rotate: '25deg' }}>
    <svg width="36" height="36" viewBox="0 0 64 64">
      <defs>
        <linearGradient id="g2" x1="0%" y1="0%" x2="100%" y2="100%">
          <stop offset="0%" stopColor="#93c5fd"/>
          <stop offset="100%" stopColor="#3b82f6"/>
        </linearGradient>
      </defs>
      <path fill="url(#g2)" d="M32 12a12 12 0 0 1 12 12c0 3.5-1 7-4 9l-8 8-8-8c-3-2-4-5.5-4-9a12 12 0 0 1 12-12zm0 32c-7 0-13 3-16 8l16 16 16-16c-3-5-9-8-16-8z"/>
    </svg>
  </span>

      {/* 2️⃣ Πορτοκαλί – σπιτάκια / ταΐστρες */}
      <span className="shape top-[85%] left-[10%] scale-125" style={{ animationDelay: '5s', rotate: '10deg' }}>
        <svg width="40" height="40" viewBox="0 0 24 24" fill="#f97316"><path d="M3 9l9-7 9 7v11a2 2 0 0 1-2 2H5a2 2 0 0 1-2-2z"/><polyline points="9 22 9 12 15 12 15 22"/></svg>
