import React from 'react';

const Button = ({
  children,
  variant = 'primary-ocean',
  size = 'md',
  disabled = false,
  onClick,
  type = 'button',
  className = ''
}) => {
  const baseStyles =
    'font-semibold rounded-xl transition-all duration-200 ' +
    'focus:outline-none focus:ring-2 focus:ring-offset-2 active:scale-95';

  const variants = {
    // θαλασσί βασικό
    'primary-ocean':
      'bg-cyan-500 text-white hover:bg-cyan-600 hover:shadow-lg hover:-translate-y-0.5 focus:ring-cyan-400',

    // δευτερεύον – γκρι
    secondary:
      'bg-gray-200 text-gray-800 hover:bg-gray-300 focus:ring-gray-400',

    // επικίνδυνο – κόκκινο
    danger:
      'bg-red-500 text-white hover:bg-red-600 hover:shadow-lg hover:-translate-y-0.5 focus:ring-red-400',

    // επιτυχία – πράσινο
    success:
      'bg-teal-500 text-white hover:bg-teal-600 hover:shadow-lg hover:-translate-y-0.5 focus:ring-teal-400',

    // outline – θαλασσί γραμμή
    outline:
      'border-2 border-cyan-500 text-cyan-600 hover:bg-cyan-50 focus:ring-cyan-400'
  };

  const sizes = {
    sm: 'px-3 py-1.5 text-sm',
    md: 'px-6 py-3',
    lg: 'px-8 py-4 text-lg'
  };

  const disabledStyles = disabled
    ? 'opacity-55 cursor-not-allowed'
    : '';

  return (
    <button
      type={type}
      onClick={onClick}
      disabled={disabled}
      className={`${baseStyles} ${variants[variant]} ${sizes[size]} ${disabledStyles} ${className}`}
    >
      {children}
    </button>
  );
};

export default Button;
